import React, { useEffect } from "react";
import L from "leaflet";
import "leaflet/dist/leaflet.css"; // Importer le CSS de Leaflet

const MapPage = () => {
  useEffect(() => {
    // Initialisation de la carte avec le centre sur Lac 1 (Tunis)
    const map = L.map("map").setView([36.8401, 10.2315], 15); // Coordonnées de Lac 1

    // Ajouter les tuiles OpenStreetMap
    L.tileLayer("https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png", {
      attribution: '&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors',
      maxZoom: 19,
    }).addTo(map);

    // Personnaliser le marqueur rouge
    const redIcon = L.icon({
      iconUrl:
        "https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon-2x-red.png", // Icône rouge
      iconSize: [25, 41], // Taille de l'icône
      iconAnchor: [12, 41], // Point d'ancrage en bas du marqueur
      popupAnchor: [1, -34], // Position du popup
      shadowUrl: "https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png", // Ombre du marqueur
      shadowSize: [41, 41],
    });

    // Ajouter un marqueur rouge à la position de Lac 1
    const marker = L.marker([36.8401, 10.2315], { icon: redIcon }).addTo(map);

    // Ajouter un popup au marqueur
    marker.bindPopup(
      "<b>Zone Lac 1</b><br>Un endroit magnifique à visiter."
    ).openPopup();

    // Nettoyer la carte à la fin
    return () => {
      map.remove();
    };
  }, []);

  return (
    <div>
      <h1 style={{ textAlign: "center", margin: "20px 0" }}>
        Carte de la Zone Lac 1
      </h1>
      <div
        id="map"
        style={{
          height: "500px",
          width: "100%",
          borderRadius: "12px",
          boxShadow: "0 4px 8px rgba(0, 0, 0, 0.2)",
        }}
      ></div>
    </div>
  );
};

export default MapPage;
