import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const Profile = () => {
  const [user, setUser] = useState(null);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem("token");

    if (!token) {
      setError("You must be logged in to view your profile.");
      navigate("/login");
      return;
    }

    axios
      .get("http://localhost:4000/profile", {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      })
      .then((response) => {
        setUser(response.data.user);
        setLoading(false);
      })
      .catch((err) => {
        setLoading(false);
        if (err.response && err.response.status === 401) {
          setError("Session expired, please log in again.");
          localStorage.removeItem("token");
          navigate("/login");
        } else {
          setError("An error occurred while fetching the profile.");
        }
      });
  }, [navigate]);

  if (loading) {
    return <div className="text-center text-gray-500 text-lg">Loading...</div>;
  }

  if (error) {
    return <div className="text-center text-red-500 text-lg">{error}</div>;
  }

  return (
    <div className="flex justify-center items-center min-h-screen bg-gray-100">
      <div className="bg-white p-8 rounded-lg shadow-lg w-full max-w-lg text-center">
        <h1 className="text-3xl font-bold text-orange-400">Your Profile</h1>
        <p className="text-gray-500 mb-4">Manage your account information</p>

        <div className="mt-5">
          <div className="border-l-4 border-orange-400 flex items-center py-2">
            <h2 className="text-lg font-semibold text-gray-700 ps-4">
              First Name:
            </h2>
            <p className="text-gray-900 font-medium ps-2">{user.firstName}</p>
          </div>

          <div className="border-l-4 border-orange-400 flex items-center py-2">
            <h2 className="text-lg font-semibold text-gray-700 ps-4">
              Last Name:
            </h2>
            <p className="text-gray-900 font-medium ps-2">{user.lastName}</p>
          </div>

          <div className="border-l-4 border-orange-400 flex items-center py-2">
            <h2 className="text-lg font-semibold text-gray-700 ps-4">Email:</h2>
            <p className="text-gray-900 font-medium ps-2">{user.email}</p>
          </div>

          <div className="border-l-4 border-orange-400 flex items-center py-2">
            <h2 className="text-lg font-semibold text-gray-700 ps-4">
              Mobile:
            </h2>
            <p className="text-gray-900 font-medium ps-2">{user.mobile}</p>
          </div>
        </div>

        <button className="mt-6 bg-orange-400 text-white px-6 py-2 rounded-lg font-semibold hover:bg-orange-500 transition">
          Edit Profile
        </button>
      </div>
    </div>
  );
};

export default Profile;
