import React from "react";

const MyMenu = ({ img, description, title, price, onAddToCart }) => {
  return (
    <div className="p-2 md:w-1/3">
      <div className="h-full border-2 border-gray-200 border-opacity-60 rounded-lg overflow-hidden">
        <img
          className="lg:h-48 md:h-36 w-full object-cover object-center"
          src={img}
          alt={title}
        />
        <div className="p-6">
          <h2 className="text-lg font-medium title-font mb-2">{title}</h2>
          <p className="leading-relaxed text-base">{description}</p>
          <div className="flex items-center justify-between mt-4">
            <span className="text-lg font-bold">{price} Dinars</span>
            <button
              onClick={onAddToCart}
              className="text-white bg-orange-400 border-0 py-2 px-4 focus:outline-none hover:bg-orange-500 rounded"
            >
              Ajouter au Panier
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default MyMenu;
