import React, { useState, useEffect } from "react";
import { useSearchParams } from "react-router-dom";
import MyMenu from "./MyMenu";
import Fade from "react-reveal/Fade";
import { FaShoppingCart, FaTrashAlt } from "react-icons/fa";

const Menu = () => {
  const [menuItems, setMenuItems] = useState([]);
  const [filteredItems, setFilteredItems] = useState([]);
  const [activeSection, setActiveSection] = useState("All");
  const [cart, setCart] = useState([]);
  const [showCart, setShowCart] = useState(false);
  const [isOrdering, setIsOrdering] = useState(false);
  const [searchParams] = useSearchParams();
  const tableNumber = searchParams.get("table") || "Unknown";

  useEffect(() => {
    const fetchMenuData = async () => {
      try {
        const response = await fetch("http://localhost:4000/api/menu");
        if (!response.ok) {
          throw new Error("Erreur lors de la récupération des données");
        }
        const data = await response.json();
    
        const formattedData = data.map((item) => ({
          ...item,
          price: parseFloat(item.price),
          img: `http://localhost:4000${item.img}`,
          // Parse les ingrédients si ils existent
          ingredients: item.ingredients ? JSON.parse(item.ingredients) : []
        }));
    
        setMenuItems(formattedData);
        setFilteredItems(formattedData);
      } catch (error) {
        console.error("Erreur :", error);
        alert("Erreur lors du chargement du menu");
      }
    };
  
    fetchMenuData();
  }, []);
  
  const filterMenu = (section) => {
    setActiveSection(section);
    if (section === "All") {
      setFilteredItems(menuItems);
    } else {
      setFilteredItems(menuItems.filter((item) => item.category === section));
    }
  };

  const handleAddToCart = (item) => {
    const itemWithNumericPrice = {
      ...item,
      price: parseFloat(item.price),
    };
    setCart((prevCart) => [...prevCart, itemWithNumericPrice]);
  };

  const handleRemoveFromCart = (index) =>
    setCart((prevCart) => prevCart.filter((_, i) => i !== index));

  const calculateTotal = () =>
    cart.reduce((total, item) => total + item.price, 0).toFixed(0);

  const handleOrder = async () => {
    if (cart.length === 0) {
      alert("Votre panier est vide !");
      return;
    }

    setIsOrdering(true);

    const orderData = {
      tableNumber,
      items: cart.map(item => ({
        id: item.id,
        title: item.title,
        price: item.price,
        ingredients: item.ingredients || []
      })),
      totalPrice: calculateTotal(),
    };

    try {
      const response = await fetch("http://localhost:4000/api/orders", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(orderData),
      });

      const result = await response.json();

      if (response.ok) {
        alert(result.message || "Commande envoyée avec succès !");
        setCart([]);
        setShowCart(false);
      } else {
        throw new Error(result.message || "Erreur lors de l'envoi de la commande");
      }
    } catch (error) {
      console.error("Erreur serveur :", error);
      alert(error.message || "Une erreur est survenue lors de la commande");
    } finally {
      setIsOrdering(false);
    }
  };

  return (
    <section className="text-gray-600 body-font">
      <div className="container lg:max-w-[1324px] px-5 py-20 mx-auto">
        <div className="flex flex-col text-center w-full mb-12 lg:mb-20">
          <p className="title-font text-orange-400 sec-heading font-medium text-3xl mb-2">
            Food Menu (Table {tableNumber})
          </p>
          <h1 className="font-nunito hero-text font-black text-black text-3xl lg:text-5xl">
            Most Popular Items
          </h1>
        </div>
        <div className="flex justify-center items-center mb-8">
          <div className="flex justify-center">
            {[
              "All",
              "Coffee",
              "Tea",
              "Juices & Cocktails",
              "Ice Cream",
              "Pastries",
              "Crepes",
              "Extras",
            ].map((section) => (
              <button
                key={section}
                onClick={() => filterMenu(section)}
                className={`mx-2 px-4 py-2 rounded-lg ${
                  activeSection === section ? "bg-orange-400 text-white" : "bg-gray-200"
                }`}
              >
                {section}
              </button>
            ))}
          </div>
          <div className="relative ml-4">
            <button 
              onClick={() => setShowCart(!showCart)} 
              className="flex items-center relative"
            >
              <FaShoppingCart size={24} className="text-orange-400 mr-2" />
              <span className="text-lg font-bold">{cart.length}</span>
              {cart.length > 0 && (
                <span className="absolute -top-2 -right-2 bg-red-500 text-white text-xs rounded-full h-5 w-5 flex items-center justify-center">
                  {cart.length}
                </span>
              )}
            </button>
            {showCart && (
              <div className="absolute right-0 top-16 bg-white shadow-lg rounded-lg w-80 p-4 z-50 max-h-96 overflow-y-auto">
                <h2 className="text-lg font-bold mb-3">Votre Panier</h2>
                <ul className="divide-y divide-gray-200">
                  {cart.map((item, index) => (
                    <li key={index} className="flex justify-between items-center py-3">
                      <div className="flex items-center w-2/3">
                        <img 
                          src={item.img} 
                          alt={item.title} 
                          className="w-12 h-12 rounded-md mr-3 object-cover" 
                        />
                        <div>
                          <span className="text-gray-700 font-medium block truncate">{item.title}</span>
                          <span className="text-gray-500 text-xs">
                            {item.ingredients?.map(i => i.name).join(', ')}
                          </span>
                        </div>
                      </div>
                      <div className="flex items-center">
                        <span className="text-gray-600 font-medium mr-3 whitespace-nowrap">
                          {item.price.toFixed(0)} DZD
                        </span>
                        <button 
                          onClick={() => handleRemoveFromCart(index)} 
                          className="text-red-500 hover:text-red-700 text-lg"
                        >
                          <FaTrashAlt />
                        </button>
                      </div>
                    </li>
                  ))}
                </ul>
                {cart.length > 0 ? (
                  <>
                    <div className="flex justify-between items-center mt-4 pt-4 border-t border-gray-200">
                      <span className="text-lg font-bold">Total :</span>
                      <span className="text-lg font-bold">{calculateTotal()} DZD</span>
                    </div>
                    <button 
                      onClick={handleOrder} 
                      disabled={isOrdering}
                      className={`mt-4 w-full bg-orange-400 text-white text-sm px-3 py-2 rounded-lg hover:bg-orange-500 transition-all ${
                        isOrdering ? 'opacity-50 cursor-not-allowed' : ''
                      }`}
                    >
                      {isOrdering ? 'Traitement en cours...' : 'Valider la Commande'}
                    </button>
                  </>
                ) : (
                  <p className="text-gray-500 text-sm mt-4 text-center">Votre panier est vide.</p>
                )}
              </div>
            )}
          </div>
        </div>
        <Fade bottom>
          <div className="flex flex-wrap -m-2">
            {filteredItems.map((item) => (
              <MyMenu
                key={item.id}
                img={item.img}
                description={item.desc}
                title={item.title}
                price={item.price}
                onAddToCart={() => handleAddToCart(item)}
              />
            ))}
          </div>
        </Fade>
      </div>
    </section>
  );
};

export default Menu;
