import React, { useState, useEffect } from 'react';

const Profile = () => {
  const [user, setUser] = useState(null);

  useEffect(() => {
    // Simulate retrieving user session from localStorage or sessionStorage
    const userSession = sessionStorage.getItem('user'); // or localStorage.getItem('user')

    if (userSession) {
      const userData = JSON.parse(userSession); // Assuming the session data is saved as a JSON string
      setUser(userData);
    }
  }, []);

  if (!user) {
    return (
      <div className="profile-container">
        <h2>Please log in to view your profile.</h2>
      </div>
    );
  }

  return (
    <div className="profile-container">
      <h2>Your Profile</h2>
      <p><strong>First Name:</strong> {user.firstName}</p>
      <p><strong>Last Name:</strong> {user.lastName}</p>
      <p><strong>Email:</strong> {user.email}</p>
      <p><strong>Mobile:</strong> {user.mobile}</p>
    </div>
  );
};

export default Profile;
