import React, { useEffect } from 'react';
import L from 'leaflet';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import 'leaflet/dist/leaflet.css'; // Assurez-vous d'importer le CSS de Leaflet

const MapPage = () => {
  // Définition de l'icône rouge en dehors du useEffect
  const redIcon = new L.Icon({
    iconUrl: 'https://upload.wikimedia.org/wikipedia/commons/a/a7/Map_marker_icon_red.svg', // Icône rouge
    iconSize: [35, 35], // Taille de l'icône
    iconAnchor: [17, 35], // Ancrage de l'icône
    popupAnchor: [0, -30], // Ancrage du popup
  });

  useEffect(() => {
    // Votre logique ici si nécessaire (par exemple pour des effets de carte)
    return () => {
      // Nettoyage ici si nécessaire
    };
  }, []);

  return (
    <div className="map-container">
      <h1 className="map-title">Carte Modernisée - Lac 1</h1>
      <MapContainer center={[36.8401, 10.2315]} zoom={15} scrollWheelZoom={false} style={{ height: '100vh', width: '100%' }}>
        {/* Tuiles de la carte avec une belle esthétique */}
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
        />
        
        {/* Marqueur avec popup */}
        <Marker position={[36.8401, 10.2315]} icon={redIcon}>
          <Popup>
            <strong>Zone Lac 1</strong>
            <p>Un endroit central et bien connu pour les visiteurs.</p>
          </Popup>
        </Marker>
      </MapContainer>
    </div>
  );
};

export default MapPage;
