import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import PagesBanner from "./PagesBanner";


import axios from "axios";

const Profile = () => {
  <PagesBanner title={'Contact Us'} />
  const [user, setUser] = useState(null);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(true); // loading state to indicate loading
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem("token");

    if (!token) {
      // If there's no token, redirect to login page
      setError("You must be logged in to view your profile.");
      navigate("/login");
      return;
    }

    // Fetch the profile using axios
    axios
      .get("http://localhost:4000/profile", {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      })
      .then((response) => {
        // Set user data if the request is successful
        setUser(response.data.user);
        setLoading(false); // Done loading
      })
      .catch((err) => {
        // Handle errors (e.g., expired token, network issues)
        setLoading(false);
        if (err.response && err.response.status === 401) {
          // Session expired or invalid token
          setError("Session expired, please log in again.");
          localStorage.removeItem("token");
          navigate("/login");
        } else {
          // General error
          setError("An error occurred while fetching the profile.");
        }
      });
  }, [navigate]);

  if (loading) {
    // Show loading state while fetching
    return <div>Loading...</div>;
  }

  if (error) {
    // Show error message if any
    return <div>{error}</div>;
  }

  return (
    <div className="profile-container">
      <h1 className="text-orange-400">Your Profile</h1>
      <div className="profile-info">
        <p><strong>First Name:</strong> {user.firstName}</p>
        <p><strong>Last Name:</strong> {user.lastName}</p>
        <p><strong>Email:</strong> {user.email}</p>
        <p><strong>Mobile:</strong> {user.mobile}</p>
      </div>
    </div>
  );
};

export default Profile;
