import React, { useState, useEffect } from "react";
import { useSearchParams } from "react-router-dom";
import MyMenu from "./MyMenu";
import Fade from "react-reveal/Fade";
import { FaShoppingCart, FaTrashAlt, FaCoins } from "react-icons/fa";

const Menu = () => {
  const [menuItems, setMenuItems] = useState([]);
  const [filteredItems, setFilteredItems] = useState([]);
  const [activeSection, setActiveSection] = useState("All");
  const [cart, setCart] = useState([]);
  const [showCart, setShowCart] = useState(false);
  const [searchParams] = useSearchParams();
  const tableNumber = searchParams.get("table") || "Unknown";
  const [userId, setUserId] = useState(null);
  const [userPoints, setUserPoints] = useState(0);

  useEffect(() => {
    const fetchMenuData = async () => {
      try {
        const response = await fetch("http://localhost:4000/api/menu");
        if (!response.ok) {
          throw new Error("Erreur lors de la récupération des données");
        }
        const data = await response.json();
    
        const formattedData = data.map((item) => ({
          ...item,
          price: parseFloat(item.price),
          img: `http://localhost:4000${item.img}`,
        }));
    
        setMenuItems(formattedData);
        setFilteredItems(formattedData);
      } catch (error) {
        console.error("Erreur :", error);
      }
    };

    const fetchUserData = async () => {
      try {
        // Récupérer l'utilisateur connecté (exemple avec token JWT)
        const token = localStorage.getItem('token');
        if (token) {
          const response = await fetch('http://localhost:4000/api/auth/me', {
            headers: {
              'Authorization': `Bearer ${token}`
            }
          });
          
          if (response.ok) {
            const user = await response.json();
            setUserId(user.id);
            fetchUserPoints(user.id);
          }
        }
      } catch (error) {
        console.error("Erreur récupération utilisateur:", error);
      }
    };

    const fetchUserPoints = async (userId) => {
      try {
        const response = await fetch(`http://localhost:4000/api/users/${userId}/points`);
        if (response.ok) {
          const data = await response.json();
          setUserPoints(data.points);
        }
      } catch (error) {
        console.error("Erreur récupération points:", error);
      }
    };

    fetchMenuData();
    fetchUserData();
  }, []);
  
  const filterMenu = (section) => {
    setActiveSection(section);
    if (section === "All") {
      setFilteredItems(menuItems);
    } else {
      setFilteredItems(menuItems.filter((item) => item.category === section));
    }
  };

  const handleAddToCart = (item) => {
    const itemWithNumericPrice = {
      ...item,
      price: parseFloat(item.price),
    };
    setCart((prevCart) => [...prevCart, itemWithNumericPrice]);
  };

  const handleRemoveFromCart = (index) =>
    setCart((prevCart) => prevCart.filter((_, i) => i !== index));

  const calculateTotal = () =>
    cart.reduce((total, item) => total + item.price, 0).toFixed(0);

  const calculatePoints = (total) => {
    return Math.floor(total / 10000) * 10;
  };

  const handleOrder = async () => {
    if (cart.length === 0) {
      alert("Votre panier est vide !");
      return;
    }

    if (!userId) {
      alert("Vous devez être connecté pour passer une commande");
      return;
    }

    const totalPrice = calculateTotal();
    const orderData = {
      tableNumber,
      items: cart,
      totalPrice,
      userId
    };

    try {
      const response = await fetch("http://localhost:4000/api/orders", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(orderData),
      });

      const result = await response.json();
      
      if (response.ok) {
        const pointsEarned = calculatePoints(totalPrice);
        alert(`Commande envoyée avec succès ! Vous avez gagné ${pointsEarned} points.`);
        setUserPoints(prev => prev + pointsEarned);
        setCart([]);
        setShowCart(false);
      } else {
        alert(result.message || "Erreur lors de l'envoi de la commande.");
      }
    } catch (error) {
      console.error("Erreur serveur :", error);
      alert("Une erreur est survenue lors de l'envoi de la commande");
    }
  };

  return (
    <section className="text-gray-600 body-font">
      <div className="container lg:max-w-[1324px] px-5 py-20 mx-auto">
        <div className="flex flex-col text-center w-full mb-12 lg:mb-20">
          <p className="title-font text-orange-400 sec-heading font-medium text-3xl mb-2">
            Food Menu (Table {tableNumber})
          </p>
          <h1 className="font-nunito hero-text font-black text-black text-3xl lg:text-5xl">
            Most Popular Items
          </h1>
        </div>
        
        <div className="flex justify-between items-center mb-8">
          <div className="flex justify-center">
            {[
              "All",
              "Coffee",
              "Tea",
              "Juices & Cocktails",
              "Ice Cream",
              "Pastries",
              "Crepes",
              "Extras",
            ].map((section) => (
              <button
                key={section}
                onClick={() => filterMenu(section)}
                className={`mx-2 px-4 py-2 rounded-lg ${
                  activeSection === section ? "bg-orange-400 text-white" : "bg-gray-200"
                }`}
              >
                {section}
              </button>
            ))}
          </div>
          
          <div className="flex items-center">
            {userId && (
              <div className="flex items-center mr-4 bg-orange-100 px-4 py-2 rounded-lg">
                <FaCoins className="text-orange-500 mr-2" />
                <span className="font-medium">Points: <span className="font-bold">{userPoints}</span></span>
              </div>
            )}
            
            <div className="relative">
              <button 
                onClick={() => setShowCart(!showCart)} 
                className="flex items-center bg-orange-100 px-4 py-2 rounded-lg hover:bg-orange-200 transition-colors"
              >
                <FaShoppingCart className="text-orange-500 mr-2" />
                <span className="font-medium">Panier <span className="font-bold">({cart.length})</span></span>
              </button>
              
              {showCart && (
                <div className="absolute right-0 top-12 bg-white shadow-lg rounded-lg w-80 p-4 z-50 max-h-96 overflow-y-auto">
                  <h2 className="text-lg font-bold mb-3 flex justify-between items-center">
                    <span>Votre Panier</span>
                    {cart.length > 0 && (
                      <span className="text-sm font-normal">
                        Points à gagner: {calculatePoints(calculateTotal())}
                      </span>
                    )}
                  </h2>
                  <ul>
                    {cart.map((item, index) => (
                      <li key={index} className="flex justify-between items-center py-2 border-b border-gray-100">
                        <div className="flex items-center w-2/3">
                          <img 
                            src={item.img} 
                            alt={item.title} 
                            className="w-12 h-12 rounded-md mr-3 object-cover" 
                          />
                          <span className="text-gray-700 font-medium truncate">{item.title}</span>
                        </div>
                        <div className="flex items-center">
                          <span className="text-gray-600 font-medium mr-3 whitespace-nowrap">
                            {typeof item.price === 'number' ? item.price.toFixed(0) : 'N/A'} Dt
                          </span>
                          <button 
                            onClick={() => handleRemoveFromCart(index)} 
                            className="text-red-500 hover:text-red-700 transition-colors"
                          >
                            <FaTrashAlt />
                          </button>
                        </div>
                      </li>
                    ))}
                  </ul>
                  
                  {cart.length > 0 ? (
                    <>
                      <div className="flex justify-between items-center mt-4 pt-4 border-t border-gray-200">
                        <span className="text-lg font-bold">Total :</span>
                        <span className="text-lg font-bold">{calculateTotal()} Dt</span>
                      </div>
                      <button 
                        onClick={handleOrder} 
                        className="mt-4 w-full bg-orange-400 text-white text-sm px-3 py-2 rounded-lg hover:bg-orange-500 transition-all flex justify-center items-center"
                      >
                        <FaShoppingCart className="mr-2" />
                        Valider la Commande
                      </button>
                    </>
                  ) : (
                    <p className="text-gray-500 text-sm mt-4 text-center">Votre panier est vide.</p>
                  )}
                </div>
              )}
            </div>
          </div>
        </div>
        
        <Fade bottom>
          <div className="flex flex-wrap -m-2">
            {filteredItems.map((item) => (
              <MyMenu
                key={item.id}
                img={item.img}
                description={item.desc}
                title={item.title}
                price={item.price}
                onAddToCart={() => handleAddToCart(item)}
              />
            ))}
          </div>
        </Fade>
      </div>
    </section>
  );
};

export default Menu;
