import React from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';

// Fonction pour créer une carte avec un marqueur rouge
const MapPage = () => {
  // Définir une icône rouge pour le marqueur
  const redIcon = new L.Icon({
    iconUrl: 'https://upload.wikimedia.org/wikipedia/commons/a/a7/Map_marker_icon_red.svg',  // URL de l'icône rouge
    iconSize: [35, 35],  // Taille de l'icône
    iconAnchor: [17, 35],  // Ancrage de l'icône
    popupAnchor: [0, -30],  // Ancrage du popup
  });

  return (
    <div className="map-container" style={{ height: '100vh', width: '100%' }}>
      {/* Conteneur de la carte */}
      <MapContainer center={[36.8401, 10.2315]} zoom={15} style={{ height: '100%', width: '100%' }}>
        {/* Ajouter le calque des tuiles OpenStreetMap */}
        <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />

        {/* Ajouter un marqueur à la position spécifiée */}
        <Marker position={[36.8401, 10.2315]} icon={redIcon}>
          {/* Ajouter un popup pour le marqueur */}
          <Popup>
            <strong>Zone Lac 1</strong>
            <p>Un endroit central et bien connu pour les visiteurs.</p>
          </Popup>
        </Marker>
      </MapContainer>
    </div>
  );
};

export default MapPage;
