import React, { useEffect } from "react";
import L from "leaflet";
import "leaflet/dist/leaflet.css"; // Import du CSS de Leaflet

const MapPage = () => {
  useEffect(() => {
    // Initialisation de la carte avec les coordonnées de Lac 1
    const map = L.map("map").setView([36.8401, 10.2315], 15); // Coordonnées de Lac 1 avec un zoom de 15

    // Ajouter les tuiles OpenStreetMap (carte libre)
    L.tileLayer("https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png", {
      attribution:
        '&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors',
      maxZoom: 19,
    }).addTo(map);

    // Créer un marqueur rouge personnalisé
    const redIcon = new L.Icon({
      iconUrl: "https://upload.wikimedia.org/wikipedia/commons/a/a7/Map_marker_icon_red.svg", // Icône rouge
      iconSize: [30, 30], // Taille de l'icône
      iconAnchor: [15, 30], // Ancrage de l'icône
      popupAnchor: [0, -30], // Ancrage du popup
    });

    // Ajouter le marqueur à la carte avec un popup
    const marker = L.marker([36.8401, 10.2315], { icon: redIcon }).addTo(map);
    marker.bindPopup("<b>Zone Lac 1</b><br>Une zone bien connue.").openPopup();

    // Nettoyer la carte au démontage du composant
    return () => {
      map.remove();
    };
  }, []);

  return (
    <div>
      <h1 style={{ textAlign: "center", margin: "20px 0" }}>Carte avec Marqueur Rouge - Lac 1</h1>
      <div
        id="map"
        style={{
          height: "500px", // Hauteur de la carte
          width: "100%", // Largeur de la carte
          borderRadius: "12px", // Coins arrondis pour un design moderne
          boxShadow: "0 4px 8px rgba(0, 0, 0, 0.2)", // Ombre subtile
        }}
      ></div>
    </div>
  );
};

export default MapPage;
