import React from "react";

const OurChefs = () => {
  // Informations des chefs
  const chefs = [
    {
      name: "Ahmed Nasri",
      role: "UI Developer", // Remplacez avec le rôle réel
      img: "chef1.jpg", // Remplacez par l'URL de l'image
      facebook: "#",
      instagram: "#",
      twitter: "#"
    },
    {
      name: "Syrine Guettat",
      role: "Backend Developer", // Remplacez avec le rôle réel
      img: "chef2.jpg", // Remplacez par l'URL de l'image
      facebook: "#",
      instagram: "#",
      twitter: "#"
    },
    {
      name: "Slim Douiri",
      role: "Fullstack Developer", // Remplacez avec le rôle réel
      img: "chef3.jpg", // Remplacez par l'URL de l'image
      facebook: "#",
      instagram: "#",
      twitter: "#"
    },
    {
      name: "Chamesseddine Ben Nasr",
      role: "Designer", // Remplacez avec le rôle réel
      img: "chef4.jpg", // Remplacez par l'URL de l'image
      facebook: "#",
      instagram: "#",
      twitter: "#"
    }
  ];

  return (
    <div className="p-5">
      <div className="bg-white shadow-lg rounded-lg p-5">
        <h2 className="text-3xl font-semibold text-center text-gray-800 mb-5">
          Meet Our Chefs
        </h2>
        <div className="flex justify-center gap-6">
          {chefs.map((chef, index) => (
            <div key={index} className="text-center bg-white shadow-lg rounded-lg p-4 w-64">
              <img
                alt={chef.name}
                className="w-32 h-32 rounded-full object-cover mb-4 mx-auto"
                src={chef.img}
              />
              <h3 className="text-xl font-medium text-gray-900">{chef.name}</h3>
              <p className="text-gray-500 mb-3">{chef.role}</p>
              <div className="flex justify-center gap-3">
                <a href={chef.facebook} target="_blank" rel="noopener noreferrer">
                  <i className="fa-brands fa-facebook py-2 px-4 rounded-full bg-blue-600 text-white"></i>
                </a>
                <a href={chef.instagram} target="_blank" rel="noopener noreferrer">
                  <i className="fa-brands fa-instagram py-2 px-4 rounded-full bg-pink-600 text-white"></i>
                </a>
                <a href={chef.twitter} target="_blank" rel="noopener noreferrer">
                  <i className="fa-brands fa-twitter py-2 px-4 rounded-full bg-blue-400 text-white"></i>
                </a>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default OurChefs;
