import React, { useState, useEffect } from "react";
import axios from "axios";
import { FaCoins, FaShoppingCart } from "react-icons/fa";
import { useNavigate } from "react-router-dom";

const Pack = () => {
  const [orderPacks, setOrderPacks] = useState([]); // Renommer packs en orderPacks
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchOrderPacks = async () => { // Renommer fetchPacks en fetchOrderPacks
      try {
        const response = await axios.get("http://localhost:4000/api/order-packs"); // Modifier l'URL de l'API
        setOrderPacks(response.data);
        setLoading(false);
      } catch (err) {
        setError("Erreur lors du chargement des packs de commande");
        setLoading(false);
        console.error(err);
      }
    };

    fetchOrderPacks();
  }, []);

  const handlePurchase = (orderPackId) => { // Renommer packId en orderPackId
    navigate(`/order/order-pack/${orderPackId}`);
  };

  // ... (le reste du code reste identique mais utilise orderPacks au lieu de packs)
  
  return (
    <div className="container mx-auto px-4 py-8">
      <h2 className="text-3xl font-bold text-center text-orange-500 mb-12">
        Nos Packs de Commande
      </h2>
      
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
        {orderPacks.map((orderPack) => ( // Remplacer packs par orderPacks
          <div 
            key={orderPack.id} 
            className="bg-white rounded-xl shadow-lg overflow-hidden hover:shadow-xl transition-shadow duration-300"
          >
            <div className="bg-orange-500 p-4">
              <h3 className="text-xl font-bold text-white text-center">{orderPack.name}</h3>
            </div>
            
            {/* ... (le reste du JSX reste identique mais utilise orderPack au lieu de pack) */}
          </div>
        ))}
      </div>
    </div>
  );
};

export default Pack;
