import React from "react";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import AboutGallary from "./AboutGallary";
import AboutStats from "./AboutStats";
import "leaflet/dist/leaflet.css";
import L from "leaflet";

// Configure the marker icon for Leaflet
const customIcon = new L.Icon({
  iconUrl: "https://cdn-icons-png.flaticon.com/512/854/854866.png",
  iconSize: [40, 40],
});

const MyAbout = () => {
  const restaurantLocation = { lat: 35.6895, lng: 139.6917 }; // Replace with your restaurant's latitude and longitude

  return (
    <>
      <section className="text-gray-600 body-font">
        <div className="container lg:max-w-[1324px] px-5 lg:py-16 mx-auto flex flex-wrap items-center">
          <AboutGallary />

          <div className="flex flex-wrap mt-10 mb-auto lg:w-1/2 sm:w-full lg:pl-5 content-start sm:pr-10">
            <AboutStats />
          </div>
        </div>
      </section>

      <section className="text-gray-600 body-font">
        <div className="container mx-auto px-5 py-16">
          <h2 className="text-2xl font-bold mb-4">Find Us Here</h2>
          <div className="h-96 w-full">
            <MapContainer
              center={restaurantLocation}
              zoom={13}
              style={{ height: "100%", width: "100%" }}
            >
              <TileLayer
                url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
              />
              <Marker position={restaurantLocation} icon={customIcon}>
                <Popup>Our Restaurant is here!</Popup>
              </Marker>
            </MapContainer>
          </div>
        </div>
      </section>
    </>
  );
};

export default MyAbout;
