import React, { useContext } from "react";
import { BrowserRouter, Route, Routes, Link } from "react-router-dom";
import { AuthContext } from '../contexts/AuthContext'; // Importer le AuthContext
import HomePage from "./Components/Pages/HomePage";
import AboutUsPage from "./Components/Pages/AboutUsPage";
import ContactUsPage from "./Components/Pages/ContactUsPage";
import MenuPage from "./Components/Pages/MenuPage";
import ServicesPage from "./Components/Pages/ServicesPage";
import Footer from "./Components/Footer/Footer";
import AuthPage from "./Components/AuthPage/AuthPage";
import ProfilePage from "./Components/Pages/Profile"; // Assurez-vous d'avoir la page de profil
import DesktopHeader from "./Components/Header/DesktopHeader"; // Si vous avez un header avec le bouton profil

const App = () => {
  // Utilisation du AuthContext pour vérifier l'état de l'utilisateur connecté
  const { currentUser } = useContext(AuthContext);

  return (
    <>
      <BrowserRouter>
        {/* Affichage du header avec un lien vers le profil si l'utilisateur est connecté */}
        <DesktopHeader />

        <Routes>
          <Route exact path='/' element={<HomePage />} />
          <Route path='/about-us' element={<AboutUsPage />} />
          <Route path='/contact-us' element={<ContactUsPage />} />
          <Route path='/menu' element={<MenuPage />} />
          <Route path='/services' element={<ServicesPage />} />

          {/* Rediriger vers le profil si l'utilisateur est connecté */}
          {currentUser ? (
            <Route path='/profile' element={<ProfilePage />} />
          ) : (
            <Route path='/auth' element={<AuthPage />} />
          )}
        </Routes>

        <Footer />
      </BrowserRouter>
    </>
  );
};

export default App;
