import React, { useEffect } from "react";
import mapboxgl from "mapbox-gl";
import "mapbox-gl/dist/mapbox-gl.css";

const MapPage = () => {
  useEffect(() => {
    // Assurez-vous d'avoir une clé d'API Mapbox valide
    mapboxgl.accessToken =
      "votre_clé_d_api_mapbox"; // Remplacez ceci par votre propre clé Mapbox

    // Créez une nouvelle instance de carte Mapbox
    const map = new mapboxgl.Map({
      container: "map", // L'élément où la carte sera affichée
      style: "mapbox://styles/mapbox/streets-v11", // Style de la carte
      center: [10.1500, 36.7980], // Coordonnées de votre restaurant
      zoom: 13, // Niveau de zoom
      scrollZoom: true, // Permet de zoomer avec la molette de la souris
    });

    // Ajouter un marqueur personnalisé pour le restaurant
    const marker = new mapboxgl.Marker({
      color: "#FF5733", // Couleur du marqueur
      scale: 1.5, // Taille du marqueur
    })
      .setLngLat([10.1500, 36.7980]) // Position du marqueur
      .setPopup(
        new mapboxgl.Popup({ offset: 25 }).setHTML(
          "<h3>Mon Restaurant</h3><p>Voici notre localisation au cœur de la ville.</p>"
        )
      )
      .addTo(map);

    // Assurez-vous que la carte est bien supprimée lors du démontage du composant
    return () => {
      map.remove();
    };
  }, []);

  return (
    <div>
      <h1 className="text-center text-2xl font-bold mb-5">
        Carte de notre Restaurant
      </h1>
      <div
        id="map"
        style={{
          height: "500px",
          width: "100%",
          borderRadius: "10px",
          boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
        }}
      ></div>
    </div>
  );
};

export default MapPage;
