import React, { useState } from "react";
import { NavLink } from "react-router-dom";
import MobileHeader from "./MobileHeader";
import AuthPage from "./AuthPage"; // Importing the AuthPage component

const DesktopHeader = () => {
  const [open, setOpen] = useState(false);
  const [showAuth, setShowAuth] = useState(false); // State to toggle AuthPage visibility

  const toggleOpen = () => setOpen(!open);
  const toggleAuth = () => setShowAuth(!showAuth); // Toggles AuthPage visibility

  return (
    <>
      <nav className="relative px-5 lg:px-10 py-5 lg:py-8 flex justify-between items-center">
        {/* Logo */}
        <NavLink className="flex title-font cursor-pointer text-yellow-500 text-2xl lg:text-3xl font-nunito font-black items-center mb-4 md:mb-0">
          <i className="fa fa-utensils me-2"></i>
          <h1>Restoran</h1>
        </NavLink>

        <div className="lg:hidden">
          <button
            className="navbar-burger flex items-center text-orange-400"
            onClick={toggleOpen}
          >
            <i className="fa-sharp fa-solid fa-bars text-2xl -mt-3"></i>
          </button>
        </div>

        <ul className="hidden absolute top-1/2 left-1/2 text-white cursor-pointer text-xl font-normal font-heebo transform -translate-y-1/2 -translate-x-1/2 lg:mx-auto lg:flex lg:items-right lg:w-auto lg:space-x-6">
          <NavLink to="/">Home</NavLink>
          <NavLink to="/about-us">About</NavLink>
          <NavLink to="/services">Services</NavLink>
          <NavLink to="/menu">Menu</NavLink>
          <NavLink to="/contact-us">Contact</NavLink>
        </ul>

        <div className="flex items-center space-x-4">
          <button
            className="inline-flex items-center bg-orange-400 border-0 py-2 px-5 focus:outline-none font-bold text-white rounded-lg"
            onClick={toggleAuth}
          >
            Connexion
          </button>
          <button className="hidden lg:block inline-flex items-center bg-orange-400 border-0 py-2 px-5 focus:outline-none font-bold text-white rounded-lg">
            Book a table
          </button>
        </div>
      </nav>

      {open && <MobileHeader toggleOpen={toggleOpen} />}
      {showAuth && <AuthPage />} {/* Display AuthPage if showAuth is true */}
    </>
  );
};

export default DesktopHeader;
