import React, { useEffect, useState } from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import L from 'leaflet';
import axios from 'axios';
import 'leaflet/dist/leaflet.css';

const MapWithApi = () => {
  const [location, setLocation] = useState(null);

  // URL de votre API
  const apiUrl = 'https://api.example.com/restaurant/location'; // Remplacer par votre URL API

  // Obtenir la localisation du restaurant depuis l'API
  useEffect(() => {
    axios
      .get(apiUrl)
      .then((response) => {
        const data = response.data;
        setLocation({
          lat: data.latitude,  // Supposons que l'API retourne latitude et longitude
          lon: data.longitude,
          name: data.name,
          description: data.description,
        });
      })
      .catch((error) => {
        console.error('Erreur lors de la récupération des données de localisation:', error);
      });
  }, []);

  // Si les données ne sont pas encore disponibles, on affiche un message de chargement
  if (!location) {
    return <div>Loading...</div>;
  }

  // Créer une icône personnalisée pour le marqueur
  const restaurantIcon = new L.Icon({
    iconUrl: 'https://image.shutterstock.com/image-vector/restaurant-location-icon-trendy-style-260nw-1087343455.jpg', // Remplacer par une icône de restaurant
    iconSize: [40, 40],
    iconAnchor: [20, 40],
    popupAnchor: [0, -40],
  });

  return (
    <div style={{ padding: '2rem', backgroundColor: '#f9f9f9' }}>
      <h1 className="text-center text-3xl font-bold text-gray-800 mb-5">
        Localisation de notre Restaurant
      </h1>

      <MapContainer
        center={[location.lat, location.lon]}
        zoom={13}
        style={{
          height: '500px',
          width: '100%',
          borderRadius: '12px',
          boxShadow: '0 6px 12px rgba(0, 0, 0, 0.1)',
        }}
      >
        <TileLayer
          url="https://api.mapbox.com/styles/v1/{id}/tiles/{z}/{x}/{y}?access_token={accessToken}"
          attribution="&copy; <a href='https://www.openstreetmap.org/copyright'>OpenStreetMap</a> contributors, Imagery © <a href='https://www.mapbox.com/'>Mapbox</a>"
          id="mapbox/streets-v11"
          accessToken="your_mapbox_access_token" // Remplacer par votre token Mapbox
        />

        <Marker position={[location.lat, location.lon]} icon={restaurantIcon}>
          <Popup>
            <div style={{ fontFamily: 'Arial, sans-serif', fontSize: '14px' }}>
              <strong>{location.name}</strong>
              <br />
              {location.description}
            </div>
          </Popup>
        </Marker>
      </MapContainer>
    </div>
  );
};



export default MapPage;
