import React, { useEffect } from "react";
import L from "leaflet";
import "leaflet/dist/leaflet.css";

const MapPage = () => {
  useEffect(() => {
    // Créer la carte
    const map = L.map("map").setView([36.7980, 10.1500], 13); // Coordonnées du restaurant

    // Ajouter le fond de carte OpenStreetMap
    L.tileLayer("https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png", {
      attribution: '© <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors',
    }).addTo(map);

    // Personnaliser l'icône du marqueur
    const restaurantIcon = new L.Icon({
      iconUrl: "https://image.shutterstock.com/image-vector/restaurant-location-icon-trendy-style-260nw-1087343455.jpg", // URL de l'icône
      iconSize: [32, 32], // Taille de l'icône
      iconAnchor: [16, 32], // L'ancrage de l'icône
      popupAnchor: [0, -32], // L'ancrage du popup par rapport à l'icône
    });

    // Ajouter un marqueur personnalisé pour la localisation du restaurant
    const marker = L.marker([36.7980, 10.1500], { icon: restaurantIcon }).addTo(map);

    // Ajouter un popup animé au marqueur
    marker.bindPopup(
      "<b>Mon Restaurant</b><br>Bienvenue chez nous ! Nous servons de délicieux plats."
    ).openPopup();

    // Animation lors de l'ouverture du popup
    marker.on("popupopen", () => {
      marker.openPopup();
    });

    // Assurez-vous que la carte est bien supprimée lors du démontage du composant
    return () => {
      map.remove();
    };
  }, []);

  return (
    <div>
      <h1 className="text-center text-2xl font-bold mb-5">Carte de notre Restaurant</h1>
      <div
        id="map"
        style={{
          height: "500px",
          width: "100%",
          borderRadius: "15px", // Coins arrondis
          boxShadow: "0 5px 15px rgba(0, 0, 0, 0.1)", // Ombre douce pour un effet flottant
        }}
      ></div>
    </div>
  );
};

export default MapPage;
