import React, { useState } from 'react';

const Profile = () => {
  const [isEditing, setIsEditing] = useState(false);
  const [profileData, setProfileData] = useState({
    firstName: 'Moez',
    lastName: 'Hajjaji',
    email: 'moez@example.com',
    password: '',
  });

  const handleEditProfile = () => {
    setIsEditing(true);
  };

  const handleSaveChanges = () => {
    setIsEditing(false);
    // Logique pour enregistrer les modifications du profil
    // Cela pourrait inclure l'appel à une API pour sauvegarder les données
    console.log('Profile updated:', profileData);
    // Réinitialiser le mot de passe après la sauvegarde
    setProfileData({ ...profileData, password: '' });
  };

  const handleChangePassword = () => {
    // Logique pour changer le mot de passe
    console.log('Password updated:', profileData.password);
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2>Profile</h2>
      <form>
        <div style={{ marginBottom: '10px' }}>
          <label>First Name</label>
          <input
            type="text"
            value={profileData.firstName}
            disabled={!isEditing}
            onChange={(e) => setProfileData({ ...profileData, firstName: e.target.value })}
            style={{ marginLeft: '10px' }}
          />
        </div>
        <div style={{ marginBottom: '10px' }}>
          <label>Last Name</label>
          <input
            type="text"
            value={profileData.lastName}
            disabled={!isEditing}
            onChange={(e) => setProfileData({ ...profileData, lastName: e.target.value })}
            style={{ marginLeft: '10px' }}
          />
        </div>
        <div style={{ marginBottom: '10px' }}>
          <label>Email</label>
          <input
            type="email"
            value={profileData.email}
            disabled={!isEditing}
            onChange={(e) => setProfileData({ ...profileData, email: e.target.value })}
            style={{ marginLeft: '10px' }}
          />
        </div>

        {isEditing ? (
          <div style={{ marginBottom: '10px' }}>
            <label>New Password</label>
            <input
              type="password"
              value={profileData.password}
              onChange={(e) => setProfileData({ ...profileData, password: e.target.value })}
              style={{ marginLeft: '10px' }}
            />
          </div>
        ) : null}

        <div style={{ marginTop: '20px' }}>
          {isEditing ? (
            <button type="button" onClick={handleSaveChanges} style={{ padding: '10px 20px', backgroundColor: 'green', color: 'white', border: 'none' }}>
              Save Changes
            </button>
          ) : (
            <button type="button" onClick={handleEditProfile} style={{ padding: '10px 20px', backgroundColor: 'blue', color: 'white', border: 'none' }}>
              Edit Profile
            </button>
          )}
        </div>
      </form>

      {!isEditing && (
        <p style={{ marginTop: '20px' }}>
          Want to change your password?{' '}
          <span
            onClick={handleEditProfile}
            style={{ cursor: 'pointer', color: 'blue', textDecoration: 'underline' }}
          >
            Change
          </span>
        </p>
      )}
    </div>
  );
};

export default Profile;
