import React, { useState, useEffect } from "react";

const Profile = () => {
  const [user, setUser] = useState(null);

  useEffect(() => {
    // Récupérer les informations de l'utilisateur à partir du localStorage ou d'une API
    const storedUser = localStorage.getItem("user");
    if (storedUser) {
      setUser(JSON.parse(storedUser)); // Récupérer et définir les données de l'utilisateur
    }
  }, []);

  if (!user) {
    return (
      <div className="text-center mt-8">
        <h2>You must be logged in to view your profile</h2>
      </div>
    );
  }

  return (
    <div className="p-8">
      <h2 className="text-2xl font-bold">Profile</h2>
      <div className="mt-4">
        <p><strong>Name:</strong> {user.firstName} {user.lastName}</p>
        <p><strong>Email:</strong> {user.email}</p>
        <p><strong>Mobile:</strong> {user.mobile}</p>
      </div>
    </div>
  );
};

export default Profile;
