import React, { useEffect } from "react";

const MapPage = () => {
  useEffect(() => {
    // Charger la carte après que le composant a été monté
    const initializeMap = () => {
      const map = new window.google.maps.Map(document.getElementById("map"), {
        center: { lat: 36.8401, lng: 10.2315 }, // Coordonnées de Lac 1
        zoom: 15, // Niveau de zoom
      });

      // Ajouter un marqueur rouge
      new window.google.maps.Marker({
        position: { lat: 36.8401, lng: 10.2315 }, // Coordonnées de Lac 1
        map: map,
        title: "Zone Lac 1",
        icon: {
          url: "http://maps.google.com/mapfiles/ms/icons/red-dot.png", // Icône rouge
        },
      });
    };

    // Vérifier si l'API Google Maps est chargée
    if (!window.google) {
      const script = document.createElement("script");
      script.src = `https://maps.googleapis.com/maps/api/js?key=YOUR_API_KEY`; // Remplacez `YOUR_API_KEY` par votre clé API
      script.async = true;
      script.defer = true;
      script.onload = initializeMap;
      document.body.appendChild(script);
    } else {
      initializeMap();
    }
  }, []);

  return (
    <div>
      <h1 style={{ textAlign: "center", margin: "20px 0" }}>
        Carte de la Zone Lac 1
      </h1>
      <div
        id="map"
        style={{
          height: "500px",
          width: "100%",
          borderRadius: "12px",
          boxShadow: "0 4px 8px rgba(0, 0, 0, 0.2)",
        }}
      ></div>
    </div>
  );
};

export default MapPage;
