import React, { useState } from "react";
import { NavLink } from "react-router-dom";
import MobileHeader from "./MobileHeader";
import AuthPage from "./AuthPage";

const DesktopHeader = () => {
  const [open, setOpen] = useState(false);
  const [showAuthForm, setShowAuthForm] = useState(false);

  const toggleOpen = () => setOpen(!open);
  const toggleAuthForm = () => setShowAuthForm(!showAuthForm);

  return (
    <>
      <nav className="relative px-5 lg:px-10 py-5 lg:py-8 flex justify-between items-center">
        {/* Logo */}
        <NavLink
          className="flex title-font cursor-pointer text-yellow-500 text-2xl lg:text-3xl font-nunito font-black items-center mb-4 md:mb-0"
        >
          <i className="fa fa-utensils me-2"></i>
          <h1>Restoran</h1>
        </NavLink>

        {/* Mobile Menu Button */}
        <div className="lg:hidden">
          <button
            className="navbar-burger flex items-center text-orange-400"
            onClick={toggleOpen}
          >
            <i className="fa-sharp fa-solid fa-bars text-2xl -mt-3"></i>
          </button>
        </div>

        {/* Desktop Menu */}
        <ul className="hidden absolute top-1/2 left-1/2 text-white cursor-pointer text-xl font-normal font-heebo transform -translate-y-1/2 -translate-x-1/2 lg:mx-auto lg:flex lg:items-right lg:w-auto lg:space-x-6">
          <NavLink to={"/"}>Home</NavLink>
          <NavLink className="mr-9" to={"/about-us"}>
            About
          </NavLink>
          <NavLink className="mr-9" to={"/services"}>
            Services
          </NavLink>
          <NavLink className="mr-9" to={"/menu"}>
            Menu
          </NavLink>
          <NavLink className="mr-9" to={"/contact-us"}>
            Contact
          </NavLink>
        </ul>

        {/* Buttons */}
        <div className="flex items-center space-x-4">
          <button
            className="inline-flex items-center bg-orange-400 border-0 py-2 px-5 focus:outline-none font-bold text-white hidden lg:block"
          >
            Book a table
          </button>
          <button
            className="inline-flex items-center bg-blue-500 border-0 py-2 px-5 focus:outline-none font-bold text-white hidden lg:block"
            onClick={toggleAuthForm}
          >
            Connexion
          </button>
        </div>
      </nav>

      {/* Mobile Header */}
      {open && <MobileHeader toggleOpen={toggleOpen} />}

      {/* Auth Page Modal */}
      {showAuthForm && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center z-50">
          <div className="w-full h-full">
            <AuthPage />
            <button
              className="absolute top-4 right-4 text-white bg-red-500 hover:bg-red-600 px-4 py-2 rounded-full"
              onClick={toggleAuthForm}
            >
              Close
            </button>
          </div>
        </div>
      )}
    </>
  );
};

export default DesktopHeader;
