import React from "react";
import PropTypes from "prop-types"; // Import PropTypes pour valider les props

const MyServices = ({ description, title, icon }) => {
  return (
    <div className="xl:w-1/4 md:w-1/2 p-4">
      <div className="shadow-lg px-6 py-12 bg-white rounded-lg group duration-300 hover:bg-orange-400">
        <div className="w-10 h-10 inline-flex items-center justify-center mb-4 text-5xl rounded-full bg-orange-400 group-hover:text-white group-hover:bg-orange-600 transition-colors duration-200">
          <i className={icon} aria-label={title}></i>
        </div>
        <h2 className="text-lg font-bold text-black group-hover:text-white mb-2">
          {title}
        </h2>
        <p className="text-base text-gray-500 group-hover:text-white leading-relaxed">
          {description}
        </p>
      </div>
    </div>
  );
};

// Validation des types de props
MyServices.propTypes = {
  description: PropTypes.string.isRequired,
  title: PropTypes.string.isRequired,
  icon: PropTypes.string.isRequired,
};

export default MyServices;
