import React, { useEffect } from "react";
import L from "leaflet";
import "leaflet/dist/leaflet.css";
import LMapboxGL from "leaflet-mapbox-gl";

const MapPage = () => {
  useEffect(() => {
    // Initialiser la carte
    const map = L.map("map", {
      center: [36.7980, 10.1500], // Coordonnées du restaurant
      zoom: 13,
      zoomControl: true,
      attributionControl: false, // Désactive le contrôle d'attribution par défaut
    });

    // Ajouter le fond de carte Mapbox avec un style personnalisé
    LMapboxGL({
      accessToken: "your_mapbox_access_token", // Remplacez par votre token Mapbox
      style: "mapbox://styles/mapbox/streets-v11", // Choisissez le style que vous préférez
    }).addTo(map);

    // Personnaliser l'icône du marqueur (Ajoutez une icône professionnelle)
    const restaurantIcon = new L.Icon({
      iconUrl: "https://image.shutterstock.com/image-vector/restaurant-location-icon-trendy-style-260nw-1087343455.jpg", // Remplacez par l'URL de l'icône
      iconSize: [40, 40], // Taille de l'icône
      iconAnchor: [20, 40], // L'ancrage de l'icône
      popupAnchor: [0, -40], // Ancrage du popup
    });

    // Ajouter un marqueur avec l'icône personnalisée
    const marker = L.marker([36.7980, 10.1500], { icon: restaurantIcon }).addTo(map);

    // Ajouter un popup avec une animation fluide
    marker
      .bindPopup(
        "<div style='font-family: Arial, sans-serif; font-size: 14px; text-align: center;'>" +
          "<strong>Mon Restaurant</strong><br>" +
          "Un endroit idéal pour savourer de délicieux plats dans un cadre agréable.<br>" +
          "<small>Adresse: 10 Rue de l'Innovation, Tunis</small>" +
        "</div>"
      )
      .openPopup();

    // Animation au clic pour ouvrir le popup
    marker.on("click", () => {
      marker.openPopup();
    });

    // Gérer la suppression de la carte lors du démontage du composant
    return () => {
      map.remove();
    };
  }, []);

  return (
    <div style={{ padding: "2rem", backgroundColor: "#f9f9f9" }}>
      <h1 className="text-center text-3xl font-bold text-gray-800 mb-5">Localisation de notre Restaurant</h1>
      <div
        id="map"
        style={{
          height: "500px",
          width: "100%",
          borderRadius: "12px", // Coins arrondis pour un design moderne
          boxShadow: "0 6px 12px rgba(0, 0, 0, 0.1)", // Ombre subtile
        }}
      ></div>
    </div>
  );
};

export default MapPage;
