import { useEffect, useState } from 'react';

// Define the MenuData array to hold the menu items fetched from the backend
export const c = [];

const fetchMenuData = async () => {
  try {
    // Fetching data from the backend API
    const response = await fetch('http://localhost:4000/api/menu');
    const data = await response.json();

    // Store the fetched data in MenuData
    MenuData.push(...data); // Pushing the fetched data to the MenuData array
    return data; // Returning the fetched data
  } catch (error) {
    console.error('Error fetching menu data:', error);
    return [];
  }
};

const MenuComponent = () => {
  const [menuItems, setMenuItems] = useState([]);

  useEffect(() => {
    // Fetch menu data once the component is mounted
    const getMenuData = async () => {
      const data = await fetchMenuData();
      setMenuItems(data); // Update the state with the fetched menu data
    };

    getMenuData();
  }, []);

  return (
    <div>
      <h1>Menu</h1>
      <ul>
        {menuItems.length > 0 ? (
          menuItems.map(item => (
            <li key={item.id}>
              <h2>{item.title}</h2>
              <p>{item.desc}</p>
              <img src={item.img} alt={item.title} />
              <p>Price: {item.price} TND</p>
              <p>Category: {item.category}</p>
              <h3>Ingredients</h3>
              <ul>
                {JSON.parse(item.ingredients).map((ingredient, index) => (
                  <li key={index}>
                    {ingredient.name}: {ingredient.quantity} {ingredient.unit}
                  </li>
                ))}
              </ul>
            </li>
          ))
        ) : (
          <p>Loading menu...</p>
        )}
      </ul>
    </div>
  );
};

export default MenuComponent;
