import React, { useState } from "react";

// Composant MyServices qui affiche chaque service
const MyServices = ({ description, title, icon }) => {
  return (
    <div className='xl:w-1/4 md:w-1/2 p-4'>
      <div className='shadow-lg px-6 group bg-white py-12 duration-1000 hover:bg-orange-400 rounded-lg'>
        <div className='w-10 h-10 inline-flex group-hover:text-white text-orange-400 text-5xl items-center justify-center rounded-full mb-4'>
          <i className={icon}></i>
        </div>
        <h2 className='text-lg font-bold group-hover:text-white text-black title-font mb-2'>
          {title}
        </h2>
        <p className='leading-relaxed group-hover:text-white text-gray-500 text-base'>
          {description}
        </p>
      </div>
    </div>
  );
};

// Données des services
export const ServicesData = [
  {
    id: 1,
    title: 'Master Chefs',
    desc: 'Diam elitr kasd sed at elitr sed ipsum justo dolor sed clita amet diam',
    icon: 'fa fa-hat-chef',
  },
  {
    id: 2,
    title: 'Quality Food',
    desc: 'Diam elitr kasd sed at elitr sed ipsum justo dolor sed clita amet diam',
    icon: 'fa fa-utensils',
  },
  {
    id: 3,
    title: 'Online Order',
    desc: 'Diam elitr kasd sed at elitr sed ipsum justo dolor sed clita amet diam',
    icon: 'fa fa-cart-plus',
  },
  {
    id: 4,
    title: '24/7 Services',
    desc: 'Diam elitr kasd sed at elitr sed ipsum justo dolor sed clita amet diam',
    icon: 'fa fa-headset',
  },
];

// Composant principal Services
const Services = () => {
  const [serviceDetails, setServiceDetails] = useState(ServicesData);

  return (
    <section className='text-gray-600 body-font'>
      <div className='container lg:max-w-[1324px] px-5 py-12 mx-auto'>
        <div className='flex flex-wrap -m-4'>
          {serviceDetails.map((item) => {
            return (
              <MyServices
                key={item.id}
                description={item.desc}
                title={item.title}
                icon={item.icon}
              />
            );
          })}
        </div>
      </div>
    </section>
  );
};

export default Services;
