import React, { useEffect } from "react";
import L from "leaflet";
import "leaflet/dist/leaflet.css";

const MapPage = () => {
  useEffect(() => {
    // Vérifiez si la carte est déjà initialisée
    if (document.getElementById("map")._leaflet_id) {
      return; // La carte est déjà initialisée, ne rien faire
    }

    // Initialisation de la carte
    const map = L.map("map").setView([36.7980, 10.1500], 13); // Remplacez par les coordonnées du restaurant

    // Ajouter une couche de fond (carte OpenStreetMap)
    L.tileLayer("https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png", {
      attribution:
        'Map data &copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors',
    }).addTo(map);

    // Ajouter un marqueur pour la localisation du restaurant
    L.marker([36.7980, 10.1500])
      .addTo(map)
      .bindPopup("<b>Mon Restaurant</b><br>Voici notre emplacement.")
      .openPopup();

    // Optionnel : Retourner la carte pour la nettoyer si nécessaire à un moment donné
    return () => {
      map.remove();
    };
  }, []); // Le tableau vide [] garantit que l'effet se déclenche une seule fois lors du montage du composant

  return (
    <div>
      <h1 className="text-center text-2xl font-bold mb-5">Carte du Restaurant</h1>
      <div
        id="map"
        style={{ height: "500px", width: "100%" }}
      ></div>
    </div>
  );
};

export default MapPage;
