import React, { useState, useEffect } from 'react';
import L from 'leaflet';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';

const MapPage = () => {
  const [redIcon, setRedIcon] = useState(null);

  useEffect(() => {
    // Définir l'icône rouge
    setRedIcon(new L.Icon({
      iconUrl: 'https://upload.wikimedia.org/wikipedia/commons/a/a7/Map_marker_icon_red.svg', // Icône rouge
      iconSize: [35, 35], // Taille de l'icône
      iconAnchor: [17, 35], // Ancrage de l'icône
      popupAnchor: [0, -30], // Ancrage du popup
    }));
  }, []);

  return (
    <div className="map-container">
      <h1 className="map-title">Carte Modernisée - Lac 1</h1>
      {/* Vérifiez si l'icône est prête avant de rendre la carte */}
      {redIcon && (
        <MapContainer center={[36.8401, 10.2315]} zoom={15} scrollWheelZoom={false} style={{ height: '100vh', width: '100%' }}>
          <TileLayer
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
          />
          
          <Marker position={[36.8401, 10.2315]} icon={redIcon}>
            <Popup>
              <strong>Zone Lac 1</strong>
              <p>Un endroit central et bien connu pour les visiteurs.</p>
            </Popup>
          </Marker>
        </MapContainer>
      )}
    </div>
  );
};

export default MapPage;
