import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

const Profile = () => {
  const [user, setUser] = useState(null);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    // Get the token from localStorage
    const token = localStorage.getItem('token');

    // If no token, redirect to login page
    if (!token) {
      setError("You must be logged in to view your profile.");
      navigate("/login");
      return;
    }

    // Fetch user profile from the backend
    fetch("/profile", {
      method: "GET",
      headers: {
        "Authorization": `Bearer ${token}`, // Send token as Authorization header
      },
    })
      .then((response) => {
        if (response.status === 401) {
          setError("Session expired, please log in again.");
          localStorage.removeItem("token");
          navigate("/login");
        } else if (response.ok) {
          return response.json();
        } else {
          return response.text().then((text) => {
            throw new Error(text || "Failed to fetch user profile");
          });
        }
      })
      .then((data) => {
        setUser(data.user); // Set the user data
      })
      .catch((err) => {
        console.error("Fetch error:", err);
        setError(`An error occurred while fetching the profile: ${err.message}`);
      });
  }, [navigate]); // Re-run effect on navigate change

  // Show loading or error state before displaying profile data
  if (error) {
    return <div>{error}</div>;
  }

  if (!user) {
    return <div>Loading...</div>;
  }

  return (
    <div className="profile-container">
      <h1 className="text-orange-400">Your Profile</h1>
      <div className="profile-info">
        <p><strong>First Name:</strong> {user.firstName}</p>
        <p><strong>Last Name:</strong> {user.lastName}</p>
        <p><strong>Email:</strong> {user.email}</p>
        <p><strong>Mobile:</strong> {user.mobile}</p>
      </div>
    </div>
  );
};

export default Profile;
