import React from "react";
import AboutGallary from "./AboutGallary";
import AboutStats from "./AboutStats";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import L from "leaflet";

// Icône personnalisée pour le marqueur
const customIcon = new L.Icon({
  iconUrl: "https://cdn-icons-png.flaticon.com/512/3448/3448590.png", // Icône personnalisée
  iconSize: [38, 38], // Taille de l'icône
});

const MyAbout = () => {
  // Coordonnées du restaurant
  const restaurantLocation = [36.8065, 10.1815]; // Exemple : Tunis, Tunisie

  return (
    <>
      <section className='text-gray-600 body-font'>
        <div className='container lg:max-w-[1324px] px-5 lg:py-16 mx-auto flex flex-wrap items-center'>
          <AboutGallary />

          <div className='flex flex-wrap mt-10 mb-auto lg:w-1/2 sm:w-full lg:pl-5 content-start sm:pr-10'>
            <AboutStats />
          </div>
        </div>

        {/* Section de la carte */}
        <div className="container lg:max-w-[1324px] px-5 py-16 mx-auto">
          <h2 className="text-2xl font-bold text-center mb-5">Our Restaurant Location</h2>
          <div className="h-[500px] w-full">
            <MapContainer
              center={restaurantLocation}
              zoom={15}
              style={{ height: "100%", width: "100%" }}
            >
              {/* Couche de tuiles (carte de base) */}
              <TileLayer
                url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
              />
              {/* Marqueur avec popup */}
              <Marker position={restaurantLocation} icon={customIcon}>
                <Popup>
                  <strong>Restaurant Name</strong>
                  <br />
                  Address: Example Street, Tunis
                </Popup>
              </Marker>
            </MapContainer>
          </div>
        </div>
      </section>
    </>
  );
};

export default MyAbout;
