import React from "react";

const OurChefs = () => {
  // Informations des chefs
  const chefs = [
    {
      name: "Ahmed Nasri",
      role: "UI Developer", // Remplacez avec le rôle réel
      img: "chef1.jpg", // Remplacez par l'URL de l'image
      facebook: "#",
      instagram: "#",
      twitter: "#"
    },
    {
      name: "Syrine Guettat",
      role: "Backend Developer", // Remplacez avec le rôle réel
      img: "chef2.jpg", // Remplacez par l'URL de l'image
      facebook: "#",
      instagram: "#",
      twitter: "#"
    },
    {
      name: "Slim Douiri",
      role: "Fullstack Developer", // Remplacez avec le rôle réel
      img: "chef3.jpg", // Remplacez par l'URL de l'image
      facebook: "#",
      instagram: "#",
      twitter: "#"
    },
    {
      name: "Chamesseddine Ben Nasr",
      role: "Designer", // Remplacez avec le rôle réel
      img: "chef4.jpg", // Remplacez par l'URL de l'image
      facebook: "#",
      instagram: "#",
      twitter: "#"
    }
  ];

  return (
    <div className="flex flex-wrap justify-center gap-4">
      {chefs.map((chef, index) => (
        <div key={index} className="p-3 lg:w-1/4 md:w-1/2 w-full">
          <div className="flex flex-col items-center text-center bg-white duration-1000 shadow-lg cursor-pointer group pt-4 lg:pt-9">
            <img
              alt={chef.name}
              className="flex-shrink-0 rounded-full w-56 object-cover object-center mb-4 group-hover:scale-110 duration-500 overflow-hidden"
              src={chef.img}
            />
            <div className="w-full">
              <h2 className="title-font font-medium hero-text text-2xl text-gray-900">
                {chef.name}
              </h2>
              <h3 className="text-gray-500 mb-3">{chef.role}</h3>
              <span className="inline-flex text-2xl text-white opacity-0 group-hover:mt-5 group-hover:opacity-100 duration-1000">
                <a href={chef.facebook} target="_blank" rel="noopener noreferrer">
                  <i className="fa-brands mr-2 fa-facebook py-4 px-2 rounded-t-lg bg-orange-400"></i>
                </a>
                <a href={chef.instagram} target="_blank" rel="noopener noreferrer">
                  <i className="fa-brands mr-2 fa-instagram py-4 px-2 rounded-t-lg bg-orange-400"></i>
                </a>
                <a href={chef.twitter} target="_blank" rel="noopener noreferrer">
                  <i className="fa-brands fa-twitter py-4 px-2 rounded-t-lg bg-orange-400"></i>
                </a>
              </span>
            </div>
          </div>
        </div>
      ))}
    </div>
  );
};

export default OurChefs;
