// Profile.js
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useHistory } from 'react-router-dom';

const Profile = () => {
  const [user, setUser] = useState(null);
  const history = useHistory();

  useEffect(() => {
    // Check if the user data is stored in sessionStorage
    const storedUser = sessionStorage.getItem('user');

    if (storedUser) {
      setUser(JSON.parse(storedUser)); // Set user data if session is active
    } else {
      history.push('/login'); // Redirect to login if no session
    }
  }, [history]);

  if (!user) {
    return <p>Loading...</p>; // Or any loading state while checking session
  }

  return (
    <div>
      <h1>Profile</h1>
      <p><strong>First Name:</strong> {user.firstName}</p>
      <p><strong>Last Name:</strong> {user.lastName}</p>
      <p><strong>Email:</strong> {user.email}</p>
      <p><strong>Mobile:</strong> {user.mobile}</p>
    </div>
  );
};

export default Profile;
