import React from "react";
import { GoogleMap, Marker, LoadScript } from "@react-google-maps/api";

const MapPage = () => {
  // Options de style de la carte
  const mapContainerStyle = {
    width: "100%",
    height: "500px",
    borderRadius: "12px", // Coins arrondis pour un design moderne
    boxShadow: "0 6px 12px rgba(0, 0, 0, 0.1)", // Ombre subtile
  };

  // Coordonnées du restaurant
  const center = {
    lat: 36.7980,
    lng: 10.1500,
  };

  return (
    <div style={{ padding: "2rem", backgroundColor: "#f9f9f9" }}>
      <h1 className="text-center text-3xl font-bold text-gray-800 mb-5">
        Localisation de notre Restaurant
      </h1>
      <LoadScript googleMapsApiKey="YOUR_GOOGLE_MAPS_API_KEY">
        <GoogleMap
          mapContainerStyle={mapContainerStyle}
          center={center}
          zoom={13}
        >
          {/* Ajouter un marqueur à l'emplacement du restaurant */}
          <Marker
            position={center}
            icon={{
              url: "https://upload.wikimedia.org/wikipedia/commons/f/f0/Red_Arrow_Down.svg", // Icône de flèche rouge
              scaledSize: new window.google.maps.Size(40, 40), // Taille de l'icône
            }}
          />
        </GoogleMap>
      </LoadScript>
    </div>
  );
};

export default MapPage;
