import React, { useState } from "react";

const OurChefs = () => {
  const [chefs, setChefs] = useState([
    {
      name: "Alper Kamu",
      role: "UI Developer",
      img: "", // Ajouter une image ici
      facebook: "",
      instagram: "",
      twitter: ""
    },
    {
      name: "Chef 2",
      role: "Backend Developer",
      img: "", 
      facebook: "",
      instagram: "",
      twitter: ""
    },
    {
      name: "Chef 3",
      role: "Fullstack Developer",
      img: "", 
      facebook: "",
      instagram: "",
      twitter: ""
    },
    {
      name: "Chef 4",
      role: "Designer",
      img: "",
      facebook: "",
      instagram: "",
      twitter: ""
    }
  ]);

  const handleInputChange = (index, e) => {
    const updatedChefs = [...chefs];
    updatedChefs[index][e.target.name] = e.target.value;
    setChefs(updatedChefs);
  };

  return (
    <div className="flex flex-wrap justify-center gap-4">
      {chefs.map((chef, index) => (
        <div key={index} className="p-3 lg:w-1/4 md:w-1/2 w-full">
          <div className="flex flex-col items-center text-center bg-white duration-1000 shadow-lg cursor-pointer group pt-4 lg:pt-9">
            <img
              alt="team"
              className="flex-shrink-0 rounded-full w-56 object-cover object-center mb-4 group-hover:scale-110 duration-500 overflow-hidden"
              src={chef.img || "default-image.jpg"} // Ajoutez une image par défaut si aucune image n'est fournie
            />
            <div className="w-full">
              <input
                type="text"
                name="name"
                value={chef.name}
                onChange={(e) => handleInputChange(index, e)}
                className="title-font font-medium hero-text text-2xl text-gray-900 mb-3"
              />
              <input
                type="text"
                name="role"
                value={chef.role}
                onChange={(e) => handleInputChange(index, e)}
                className="text-gray-500 mb-3"
              />
              <input
                type="text"
                name="facebook"
                value={chef.facebook}
                onChange={(e) => handleInputChange(index, e)}
                placeholder="Facebook Link"
                className="mb-2 p-2 rounded bg-gray-100 w-full"
              />
              <input
                type="text"
                name="instagram"
                value={chef.instagram}
                onChange={(e) => handleInputChange(index, e)}
                placeholder="Instagram Link"
                className="mb-2 p-2 rounded bg-gray-100 w-full"
              />
              <input
                type="text"
                name="twitter"
                value={chef.twitter}
                onChange={(e) => handleInputChange(index, e)}
                placeholder="Twitter Link"
                className="mb-2 p-2 rounded bg-gray-100 w-full"
              />
            </div>
          </div>
        </div>
      ))}
    </div>
  );
};

export default OurChefs;
