import React from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';

const MapPage = () => {
  // Coordonnées du restaurant
  const position = [36.7980, 10.1500];

  // Créer une icône personnalisée pour le marqueur
  const restaurantIcon = new L.Icon({
    iconUrl: 'https://image.shutterstock.com/image-vector/restaurant-location-icon-trendy-style-260nw-1087343455.jpg', // Remplacer par une icône de restaurant
    iconSize: [40, 40], // Taille de l'icône
    iconAnchor: [20, 40], // Positionnement de l'icône par rapport au marqueur
    popupAnchor: [0, -40], // Position du popup
  });

  return (
    <div style={{ padding: '2rem', backgroundColor: '#f9f9f9' }}>
      <h1 className="text-center text-3xl font-bold text-gray-800 mb-5">
        Localisation de notre Restaurant
      </h1>

      <MapContainer
        center={position}
        zoom={13}
        style={{
          height: '500px',
          width: '100%',
          borderRadius: '12px',
          boxShadow: '0 6px 12px rgba(0, 0, 0, 0.1)',
        }}
      >
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          attribution="&copy; <a href='https://www.openstreetmap.org/copyright'>OpenStreetMap</a> contributors"
        />

        <Marker position={position} icon={restaurantIcon}>
          <Popup>
            <div style={{ fontFamily: 'Arial, sans-serif', fontSize: '14px' }}>
              <strong>Mon Restaurant</strong>
              <br />
              Un endroit idéal pour savourer de délicieux plats dans un cadre agréable.
              <br />
              <small>Adresse: 10 Rue de l'Innovation, Tunis</small>
            </div>
          </Popup>
        </Marker>
      </MapContainer>
    </div>
  );
};

export default MapPage;
