import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom"; // Remplace useHistory par useNavigate

const Profile = () => {
  const [user, setUser] = useState(null);
  const [error, setError] = useState(null);
  const navigate = useNavigate(); // Utilise useNavigate au lieu de useHistory

  useEffect(() => {
    // Vérification du token dans le localStorage
    const token = localStorage.getItem('token');
    
    if (!token) {
      setError("You must log in first.");
      navigate("/login");  // Rediriger vers la page de login si pas de token
      return;
    }

    // Si un token est présent, vérifier sa validité via une requête au backend
    fetch('/profile', {
      method: 'GET',
      headers: {
        'Authorization': `Bearer ${token}`,
      },
    })
      .then((response) => {
        if (response.status === 401) {
          setError("Session expired, please log in again.");
          localStorage.removeItem('token');  // Supprimer un token expiré
          navigate("/login");
        } else if (response.ok) {
          return response.json();
        } else {
          throw new Error("Failed to fetch user profile");
        }
      })
      .then((data) => {
        setUser(data.user);  // Assumer que le backend retourne l'objet utilisateur dans "user"
      })
      .catch((err) => {
        setError("An error occurred while fetching the profile.");
        console.error(err);
      });
  }, [navigate]); // Utilisation de navigate au lieu de history

  if (error) {
    return <div>{error}</div>;
  }

  if (!user) {
    return <div>Loading...</div>; // Chargement des données de l'utilisateur
  }

  return (
    <div className="profile-container">
      <h1 className="text-orange-400">Your Profile</h1>
      <div className="profile-info">
        <p><strong>First Name:</strong> {user.firstName}</p>
        <p><strong>Last Name:</strong> {user.lastName}</p>
        <p><strong>Email:</strong> {user.email}</p>
        <p><strong>Mobile:</strong> {user.mobile}</p>
      </div>
    </div>
  );
};

export default Profile;
