import React, { useState, useEffect } from "react";
import { useSearchParams } from "react-router-dom";
import { FaShoppingCart, FaTrashAlt } from "react-icons/fa";
import Fade from "react-reveal/Fade";

const MenuItem = ({ img, title, description, price, onAddToCart }) => (
  <div className="p-2 lg:w-1/3 md:w-1/2 w-full">
    <div className="h-full flex items-center border-gray-200 border p-4 rounded-lg">
      <img
        alt={title}
        className="w-16 h-16 bg-gray-100 object-cover object-center flex-shrink-0 rounded-full mr-4"
        src={img}
      />
      <div className="flex-grow">
        <h2 className="text-gray-900 title-font font-medium">{title}</h2>
        <p className="text-gray-500 text-sm line-clamp-2">{description}</p>
        <div className="flex justify-between items-center mt-2">
          <span className="text-orange-400 font-bold">{price.toFixed(0)} DZD</span>
          <button
            onClick={onAddToCart}
            className="bg-orange-400 text-white px-3 py-1 rounded text-sm hover:bg-orange-500 transition"
          >
            Ajouter
          </button>
        </div>
      </div>
    </div>
  </div>
);

const Menu = () => {
  const [menuItems, setMenuItems] = useState([]);
  const [filteredItems, setFilteredItems] = useState([]);
  const [activeSection, setActiveSection] = useState("All");
  const [cart, setCart] = useState([]);
  const [showCart, setShowCart] = useState(false);
  const [isOrdering, setIsOrdering] = useState(false);
  const [searchParams] = useSearchParams();
  const tableNumber = searchParams.get("table") || "Unknown";

  useEffect(() => {
    const fetchMenuData = async () => {
      try {
        const response = await fetch("http://localhost:4000/api/menu");
        if (!response.ok) throw new Error("Erreur réseau");
        
        const data = await response.json();
        setMenuItems(data);
        setFilteredItems(data);
      } catch (error) {
        console.error("Erreur de chargement:", error);
        alert("Erreur de chargement du menu");
      }
    };

    fetchMenuData();
  }, []);

  const filterMenu = (section) => {
    setActiveSection(section);
    setFilteredItems(section === "All" ? menuItems : menuItems.filter(item => item.category === section));
  };

  const handleAddToCart = (item) => {
    setCart(prev => [...prev, {
      id: item.id,
      title: item.title,
      price: item.price,
      img: item.img,
      ingredients: item.ingredients || []
    }]);
  };

  const handleRemoveFromCart = (index) => {
    setCart(prev => prev.filter((_, i) => i !== index));
  };

  const calculateTotal = () => cart.reduce((total, item) => total + item.price, 0);

  const handleOrder = async () => {
    if (cart.length === 0) return alert("Panier vide");
    
    setIsOrdering(true);
    try {
      const orderData = {
        tableNumber,
        items: cart,
        totalPrice: calculateTotal()
      };

      const response = await fetch("http://localhost:4000/api/orders", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(orderData)
      });

      const result = await response.json();
      
      if (!response.ok) throw new Error(result.message || "Erreur de commande");
      
      alert(result.message);
      setCart([]);
      setShowCart(false);
    } catch (error) {
      console.error("Erreur:", error);
      alert(`Erreur: ${error.message}`);
    } finally {
      setIsOrdering(false);
    }
  };

  return (
    <section className="text-gray-600 body-font">
      <div className="container lg:max-w-[1324px] px-5 py-20 mx-auto">
        <div className="flex flex-col text-center w-full mb-12 lg:mb-20">
          <p className="title-font text-orange-400 sec-heading font-medium text-3xl mb-2">
            Menu (Table {tableNumber})
          </p>
          <h1 className="font-nunito hero-text font-black text-black text-3xl lg:text-5xl">
            Nos Spécialités
          </h1>
        </div>

        <div className="flex justify-center items-center mb-8">
          <div className="flex flex-wrap justify-center gap-2">
            {["All", "Coffee", "Tea", "Juices", "Ice Cream", "Pastries"].map(section => (
              <button
                key={section}
                onClick={() => filterMenu(section)}
                className={`px-4 py-2 rounded-lg ${
                  activeSection === section 
                    ? "bg-orange-400 text-white" 
                    : "bg-gray-200 hover:bg-gray-300"
                }`}
              >
                {section}
              </button>
            ))}
          </div>

          <div className="relative ml-4">
            <button 
              onClick={() => setShowCart(!showCart)}
              className="flex items-center relative p-2"
            >
              <FaShoppingCart size={24} className="text-orange-400" />
              {cart.length > 0 && (
                <span className="absolute -top-1 -right-1 bg-red-500 text-white text-xs rounded-full h-5 w-5 flex items-center justify-center">
                  {cart.length}
                </span>
              )}
            </button>

            {showCart && (
              <div className="absolute right-0 top-12 bg-white shadow-xl rounded-lg w-80 p-4 z-50 max-h-96 overflow-y-auto border border-gray-200">
                <h2 className="text-xl font-bold mb-3 text-gray-800">Votre Panier</h2>
                
                {cart.length === 0 ? (
                  <p className="text-gray-500 text-center py-4">Votre panier est vide</p>
                ) : (
                  <>
                    <ul className="divide-y divide-gray-200">
                      {cart.map((item, index) => (
                        <li key={index} className="py-3 flex justify-between items-center">
                          <div className="flex items-center">
                            <img 
                              src={item.img} 
                              alt={item.title} 
                              className="w-10 h-10 rounded-md mr-3 object-cover"
                            />
                            <div>
                              <p className="font-medium text-gray-800">{item.title}</p>
                              <p className="text-sm text-gray-500">
                                {item.price.toFixed(0)} DZD
                              </p>
                            </div>
                          </div>
                          <button 
                            onClick={() => handleRemoveFromCart(index)}
                            className="text-red-500 hover:text-red-700 p-1"
                          >
                            <FaTrashAlt />
                          </button>
                        </li>
                      ))}
                    </ul>

                    <div className="border-t border-gray-200 pt-3 mt-3">
                      <div className="flex justify-between items-center mb-4">
                        <span className="font-bold">Total:</span>
                        <span className="font-bold text-lg">
                          {calculateTotal().toFixed(0)} DZD
                        </span>
                      </div>
                      <button
                        onClick={handleOrder}
                        disabled={isOrdering}
                        className={`w-full bg-orange-400 text-white py-2 rounded-lg hover:bg-orange-500 transition ${
                          isOrdering ? "opacity-70 cursor-not-allowed" : ""
                        }`}
                      >
                        {isOrdering ? "En cours..." : "Valider la Commande"}
                      </button>
                    </div>
                  </>
                )}
              </div>
            )}
          </div>
        </div>

        <Fade bottom>
          <div className="flex flex-wrap -m-2">
            {filteredItems.map(item => (
              <MenuItem
                key={item.id}
                img={item.img}
                title={item.title}
                description={item.desc}
                price={item.price}
                onAddToCart={() => handleAddToCart(item)}
              />
            ))}
          </div>
        </Fade>
      </div>
    </section>
  );
};

export default Menu;
