import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Orders = () => {
  const [orders, setOrders] = useState([]);

  useEffect(() => {
    // Récupérer les commandes de l'API
    axios
      .get('http://localhost:4000/api/orders')  // URL de votre API
      .then(response => {
        setOrders(response.data);  // Stocker les commandes dans l'état
      })
      .catch(error => {
        console.error('Erreur lors de la récupération des commandes', error);
      });
  }, []);

  return (
    <div>
      <h1>Liste des Commandes</h1>
      <table border="1">
        <thead>
          <tr>
            <th>Table Number</th>
            <th>Item Title</th>
            <th>Description</th>
            <th>Image</th>
            <th>Price</th>
          </tr>
        </thead>
        <tbody>
          {orders.map(order => (
            order.items.map(item => (
              <tr key={item.id}>
                <td>{order.table_number}</td>
                <td>{item.title}</td>
                <td>{item.desc}</td>
                <td>
                  <img 
                    src={item.img} 
                    alt={item.title} 
                    style={{ width: '100px', height: 'auto' }}  // Modifier la taille de l'image
                  />
                </td>
                <td>{item.price / 100} USD</td>  {/* Affichage du prix en dollars (divisé par 100) */}
              </tr>
            ))
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Orders;
