import React, { useEffect, useState } from "react";
import { useSearchParams } from "react-router-dom";
import MyMenu from "./MyMenu"; // Assuming this component handles individual menu item display
import Fade from "react-reveal/Fade";
import { FaShoppingCart, FaTrashAlt } from "react-icons/fa";

const Menu = () => {
  const [menuItems, setMenuItems] = useState([]);
  const [activeSection, setActiveSection] = useState("All");
  const [cart, setCart] = useState([]);
  const [showCart, setShowCart] = useState(false);
  const [searchParams] = useSearchParams();
  const tableNumber = searchParams.get("table") || "Unknown";

  useEffect(() => {
    fetch("http://localhost:4000/api/menu")
      .then((response) => response.json())
      .then((data) => {
        setMenuItems(data);
      })
      .catch((error) => console.error("Erreur :", error));
  }, []);

  const filterMenu = (section) => {
    setActiveSection(section);
    setMenuItems(
      section === "All"
        ? menuItems // Use fetched menuItems here
        : menuItems.filter((item) => item.category === section)
    );
  };

  const handleAddToCart = (item) => setCart((prevCart) => [...prevCart, item]);

  const handleRemoveFromCart = (index) =>
    setCart((prevCart) => prevCart.filter((_, i) => i !== index));

  const calculateTotal = () =>
    cart.reduce((total, item) => total + item.price, 0).toFixed(0);

  const handleOrder = async () => {
    if (cart.length === 0) {
      alert("Votre panier est vide !");
      return;
    }

    const orderData = {
      tableNumber,
      items: cart,
      totalPrice: calculateTotal(),
    };

    try {
      const response = await fetch("http://localhost:4000/api/orders", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(orderData),
      });

      if (response.ok) {
        alert("Commande envoyée avec succès !");
        setCart([]);
        setShowCart(false);
      } else {
        alert("Erreur lors de l'envoi de la commande.");
      }
    } catch (error) {
      console.error("Erreur serveur :", error);
    }
  };

  return (
    <section className="text-gray-600 body-font">
      <div className="container lg:max-w-[1324px] px-5 py-20 mx-auto">
        {/* ... (Rest of the JSX remains the same) */}
        <Fade bottom>
          <div className="flex flex-wrap -m-2">
            {menuItems.map((item) => ( // Use menuItems from API
              <MyMenu
                key={item.id}
                img={item.img}
                description={item.desc}
                title={item.title}
                price={item.price}
                onAddToCart={() => handleAddToCart(item)}
              />
            ))}
          </div>
        </Fade>
      </div>
    </section>
  );
};

export default Menu;
