import React, { useEffect } from "react";
import L from "leaflet";
import "leaflet/dist/leaflet.css"; // Assurez-vous que le CSS de Leaflet est bien importé

const MapPage = () => {
  useEffect(() => {
    // Initialiser la carte avec les coordonnées et le zoom
    const map = L.map("map").setView([36.7980, 10.1500], 13); // Coordonnées du restaurant

    // Ajouter un fond de carte avec OpenStreetMap
    L.tileLayer(
      "https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png", // OpenStreetMap URL
      {
        attribution:
          '&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors',
        maxZoom: 19,
        minZoom: 10,
      }
    ).addTo(map);

    // Personnaliser l'icône du marqueur avec une flèche rouge
    const arrowIcon = new L.Icon({
      iconUrl: "https://upload.wikimedia.org/wikipedia/commons/f/f0/Red_Arrow_Down.svg", // Icône d'une flèche rouge
      iconSize: [40, 40], // Taille de l'icône
      iconAnchor: [20, 40], // Ancrage de l'icône
      popupAnchor: [0, -40], // Ancrage du popup
    });

    // Ajouter un marqueur avec l'icône de la flèche rouge
    const marker = L.marker([36.7980, 10.1500], { icon: arrowIcon }).addTo(map);

    // Ajouter un popup avec des informations supplémentaires
    marker
      .bindPopup(
        "<div style='font-family: Arial, sans-serif; font-size: 14px; text-align: center;'>" +
          "<strong>Mon Restaurant</strong><br>" +
          "Un endroit idéal pour savourer de délicieux plats dans un cadre agréable.<br>" +
          "<small>Adresse: 10 Rue de l'Innovation, Tunis</small>" +
        "</div>"
      )
      .openPopup();

    // Gérer la suppression de la carte lors du démontage du composant
    return () => {
      map.remove();
    };
  }, []);

  return (
    <div style={{ padding: "2rem", backgroundColor: "#f9f9f9" }}>
      <h1 className="text-center text-3xl font-bold text-gray-800 mb-5">
        Localisation de notre Restaurant
      </h1>
      <div
        id="map"
        style={{
          height: "500px",
          width: "100%",
          borderRadius: "12px", // Coins arrondis pour un design moderne
          boxShadow: "0 6px 12px rgba(0, 0, 0, 0.1)", // Ombre subtile
        }}
      ></div>
    </div>
  );
};

export default MapPage;
